import React,{useState,useEffect} from 'react'
import Layout from '../../components/Layout/Layout'
import { toast } from 'react-hot-toast';
import axios from 'axios';
import {Select} from 'antd'
import AdminMenu from '../../components/Layout/AdminMenu'
const{Option}=Select
const CreateProduct = () => {
  const[categories,setCategories]=useState([])
  const[image,setImage]=useState('')
  const[name,setName]=useState('')
  const[description,setDescription]=useState('')
  const[price,setPrice]=useState('')
  const[quantity,setQuantity]=useState('')
  const[category,setCategory]=useState('')
  const[shipping,setShipping]=useState('')
  
  //get all category
  const getAllCategory = async () => {
    try {
      const { data } = await axios.get(`${process.env.REACT_APP_API}/api/v1/category/get-category`)
      console.log(data);
      if (data?.success) {
        setCategories(data?.category);
      }
    } catch (error) {
      console.log(error);
      toast.error("Something Went Wrong in Retrieving Category");
    }
  };
  useEffect(() => {
    console.log("hello")
    getAllCategory();
  },[])
  return (
    <Layout title="Dashboard - Create Product">
      <div className='container-fluid m-4 p-4'>
        <div className='row'>
          <div className='col-md-3'>
            <AdminMenu/>
          </div>
          <div className='col-md-9'>
            <h1>Create Product</h1>
            <div className='m-1 w-75'>
              <Select bordered={false} placeholder="Select a category" 
              size='large' showSearch 
              className='form-select mb-3' onChange={(value)=>{setCategory(value)}}>
                {categories?.map(c =>(
                  <Option value={c.name} key={c._id}>{c.name}</Option>
                ))}
              </Select>
              <div className='mb-3'>
                <label className='btn btn-outline-secondary'>
                  {image ? image.name :"Upload Image"}
                  <input type="file" name='image' accept='image/*' onChange={(e)=>{
                    setImage(e.target.files[0])
                  }} hidden></input>
                </label>
              </div>
              <div className='mb-3'>
                  {image && (<div className='text-center'>
                    <img src={URL.createObjectURL(image)} alt='Product'></img>
                  </div>)}
              </div>
            </div>
          </div>
        </div>
      </div>
    </Layout>
  )
}

export default CreateProduct
