import { useState, useEffect } from "react"
import Layout from '../../components/Layout/Layout'
import { Link } from 'react-router-dom'
const Login = () => {
    const [email, setEmail] = useState('')
    const [password, setPassword] = useState('')
    const handleSubmit = async (e) => {
        e.preventDefault()
        console.log(email + " " + password);
        await login(email, password)
    }
    return (
        <Layout title="Register -BuyFresh">
            <form className="login" onSubmit={handleSubmit}>
                <div className="socialIcons"><h2>Log In</h2></div>
                <label>Email address:</label>
                <input
                    type="email"
                    onChange={(e) => setEmail(e.target.value)}
                    value={email}
                />
                <label>Password:</label>
                <input
                    type="password"
                    onChange={(e) => setPassword(e.target.value)}
                    value={password}
                />
                <div className="socialIcons"><button className="but" disabled={isLoading}>Log in</button></div>
                {error && <div className="error">{error}</div>}
                {/* <div className="socialIcons">
                    <h5>----------Or Connect With---------- </h5>
                    <div>
                        <img src={goog} onClick={googleAuth} className="google" />
                    </div>
                </div> */}
                <div className="socialIcons"><Link className="blue-link" to="/register">Do not have a account ?</Link></div>
            </form>
        </Layout>
    )
}

export default Login
