import React, { useState, useEffect } from "react";
import Layout from "../components/Layout/Layout";
import axios from "axios";
import { useParams } from "react-router-dom";
const ProductDetails = () => {
  const params = useParams();
  const [product, setProduct] = useState({});
  const [similarProduct, setsimilarProduct] = useState([]);
  //get Product
  const getSingleProduct = async () => {
    try {
      const { data } = await axios.get(
        `${process.env.REACT_APP_API}/api/v1/product/get-product/${params.slug}`
      );
      setProduct(data?.product);
    } catch (error) {
      console.log(error);
    }
  };
  //getSimilarProduct
  const getSimilarProduct = async () => {
    try {
      const { data } = await axios.get(
        `${process.env.REACT_APP_API}/api/v1/product/related-product/${product._id}/${product.category?._id}`
      );
      setsimiProduct(data?.product);
    } catch (error) {
      console.log(error);
    }
  };
  useEffect(() => {
    if (params?.slug) {
      getSingleProduct();
    }
  }, []);
  return (
    <Layout>
      <div className="row container mt-3">
        <div className="col-md-6">
          <img
            src={`${process.env.REACT_APP_API}/api/v1/product/product-image/${product._id}`}
            className="card-img-top"
            alt={product.name}
            height={'300'}
            width={'300'}
          />
        </div>
        <div className="col-md-6 ">
            <h1 className="text-center">Product Details</h1>
            <h6> Name : {product.name}</h6>
            <h6> Description : {product.description}</h6>
            <h6> Price : {product.price}</h6>
            <h6> Category :{product?.category?.name}</h6>
            <button className="btn btn-secondary ms-2">ADD To CART</button>
        </div>
      </div>
      <div className="row">Similar Products</div>
    </Layout>
  );
};

export default ProductDetails;
