import React, { useState, useEffect } from 'react'
import Layout from '../components/Layout/Layout'
import { useAuth } from '../context/Auth'
import { toast } from 'react-hot-toast';
import axios from 'axios';
const HomePage = () => {
  const [auth, setAuth] = useAuth();
  const [products, setProducts] = useState([]);
  const [Category, setCategory] = useState([]);
  const getAllProducts = async () => {
    try {
      const { data } = await axios.get(`${process.env.REACT_APP_API}/api/v1/product/get-product`)
      console.log(data);
      if (data?.success) {
        setProducts(data?.product);
        console.log(products)
      }
    } catch (error) {
      console.log(error);
      toast.error("Something Went Wrong in Retrieving Products");
    }
  };
  useEffect(() => {
    getAllProducts();
  }, [])
  return (
    <Layout title={"All Product - Best Offers"}>
      <div className='row mt-4'>
        <div className='col-md-3'>
          <h4 className='text-center'>Filter By Category</h4>
        </div>
        <div className='col-md-9'>
          <h1 className='text-center'>All Products</h1>
          <div className='d-flex flex-wrap'>
            {products.map((product) => {
              return <div className="card m-2" style={{ width: '18rem' }} >
                <img src={`${process.env.REACT_APP_API}/api/v1/product/product-image/${product._id}`} className="card-img-top" alt={product.name} />
                <div className="card-body">
                  <h5 className="card-title">{product.name}</h5>
                  <p className="card-text">{product.description}</p>
                  <button href="#" className="btn btn-primary">More Details</button>
                  <button href="#" className="btn btn-secondary">ADD To CART</button>
                </div>
              </div>


            })}
          </div>
        </div>
      </div>
    </Layout>
  )
}

export default HomePage
