import React, { useState, useEffect } from "react";
import Layout from "../components/Layout/Layout";
import axios from "axios";
import { useParams } from "react-router-dom";
const ProductDetails = () => {
  const params = useParams();
  const [product, setProduct] = useState({});
  //get Product
  const getSingleProduct = async () => {
    try {
      const { data } = await axios.get(
        `${process.env.REACT_APP_API}/api/v1/product/get-product/${params.slug}`
      );
      setProduct(data?.product);
    } catch (error) {
      console.log(error);
    }
  };
  useEffect(() => {
    if (params?.slug) {
      getSingleProduct();
    }
  }, []);
  return (
    <Layout>
      {/* <div className="row container mt-3">
        <div className="col-md-6">
          <img
            src={`${process.env.REACT_APP_API}/api/v1/product/product-image/${product._id}`}
            className="card-img-top"
            alt={product.name}
          />
        </div>
        <div className="col-md-6"></div>
      </div>
      <div className="row">Similar Products</div> */}
      <div className="container mt-5 mb-5">
        <div className="row d-flex justify-content-center">
          <div className="col-md-10">
            <div className="card">
              <div className="row">
                <div className="col-md-6">
                  <div className="images p-3">
                    <div className="text-center p-4">
                      {" "}
                      <img
                        id="main-image"
                        src={`${process.env.REACT_APP_API}/api/v1/product/product-image/${product._id}`}
                        width={250}
                      />{" "}
                    </div>
                  </div>
                </div>
                <div className="col-md-6">
                  <div className="product p-4">
                    <div className="d-flex justify-content-between align-items-center">
                      <div className="d-flex align-items-center">
                        {" "}
                        <i className="fa fa-long-arrow-left" />{" "}
                        <span className="ml-1">Back</span>{" "}
                      </div>{" "}
            
                    </div>
                    <div className="mt-4 mb-3">
                      {" "}
                      
                      <h4 className="text-uppercase">{product.name}</h4>
                      <div className="price d-flex flex-row align-items-center">
                        {" "}
                        <span className="act-price"></span>
                        <div className="ml-2">
                          {" "}
                          <small className="dis-price">$59</small>{" "}
                          <span>40% OFF</span>{" "}
                        </div>
                      </div>
                    </div>
                    <p className="about">
                      Shop from a wide range of t-shirt from orianz. Pefect for
                      your everyday use, you could pair it with a stylish pair
                      of jeans or trousers complete the look.
                    </p>
                    <div className="sizes mt-5">
                      <h6 className="text-uppercase">Size</h6>{" "}
                      <label className="radio">
                        {" "}
                        <input
                          type="radio"
                          name="size"
                          defaultValue="S"
                          defaultChecked
                        />{" "}
                        <span>S</span>{" "}
                      </label>{" "}
                      <label className="radio">
                        {" "}
                        <input type="radio" name="size" defaultValue="M" />{" "}
                        <span>M</span>{" "}
                      </label>{" "}
                      <label className="radio">
                        {" "}
                        <input type="radio" name="size" defaultValue="L" />{" "}
                        <span>L</span>{" "}
                      </label>{" "}
                      <label className="radio">
                        {" "}
                        <input
                          type="radio"
                          name="size"
                          defaultValue="XL"
                        />{" "}
                        <span>XL</span>{" "}
                      </label>{" "}
                      <label className="radio">
                        {" "}
                        <input
                          type="radio"
                          name="size"
                          defaultValue="XXL"
                        />{" "}
                        <span>XXL</span>{" "}
                      </label>
                    </div>
                    <div className="cart mt-4 align-items-center">
                      {" "}
                      <button className="btn btn-danger text-uppercase mr-2 px-4">
                        Add to cart
                      </button>{" "}
                      <i className="fa fa-heart text-muted" />{" "}
                      <i className="fa fa-share-alt text-muted" />{" "}
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </Layout>
  );
};

export default ProductDetails;
