        if(data){
            const parsedata=JSON.parse(data);
            setAuth({
                ...auth,
                user:parsedata.user,
                token:parsedata.token
            })
        }
        //eslint-disable-next-line
    },[auth?.token])
    return (
        <CartContext.Provider value={[Cart,setCart]}>
            {children}
        </CartContext.Provider>
    )
}

